const grouped = {};

for (const item of items) {
  const data = item.json;

  // å¾åŸå§‹è³‡æ–™ç²å– category å’Œ mySource
  const category = data.category || 'æœªåˆ†é¡';
  const mySource = data.mySource || 'æœªçŸ¥';

  // å¾ HTTP å›æ‡‰ç²å– XML å…§å®¹
  const xmlContent = data.data || '';

  if (!xmlContent || xmlContent.length < 100) continue;

  if (!grouped[category]) grouped[category] = [];

  // ä½¿ç”¨æ­£å‰‡è§£æ XML
  const itemMatches = xmlContent.match(/<item[^>]*>([\s\S]*?)<\/item>/gi) || [];

  let count = 0;
  for (const itemXml of itemMatches) {
    if (count >= 5) break; // æ¯ä¾†æºæœ€å¤š 5 å‰‡

    // æå–æ¨™é¡Œ
    const titleMatch = itemXml.match(/<title[^>]*>(?:<!\[CDATA\[)?([^<\]]*?)(?:\]\]>)?<\/title>/i);
    let title = titleMatch ? titleMatch[1].trim() : '';
    if (!title || title.length < 2) continue;

    // æ¸…ç†æ¨™é¡Œä¸­çš„ Markdown ç‰¹æ®Šå­—å…ƒ
    title = title.replace(/\[/g, '(').replace(/\]/g, ')').replace(/_/g, '-');

    // æå–é€£çµ
    const linkMatch = itemXml.match(/<link[^>]*>([^<]*)<\/link>/i) || 
                      itemXml.match(/href=["']([^"']+)["']/i);
    const link = linkMatch ? linkMatch[1].trim() : '#';

    grouped[category].push({
      title: title.substring(0, 80),
      link: link,
      source: mySource
    });
    count++;
  }
}

// çµ„è£ Telegram è¨Šæ¯
const returnItems = [];

for (const [category, articles] of Object.entries(grouped)) {
  if (articles.length === 0) continue;

  let message = `*${category}*\n\n`;

  articles.slice(0, 10).forEach((art, index) => {
    message += `${index + 1}\\. [${art.title}](${art.link}) - _${art.source}_\n\n`;
  });

  returnItems.push({
